    plt.ticklabel_format(axis='y', style='plain', useOffset=False)

    #plt.ylim(0, 1.1)
    plt.title(protocol)
    plt.xlabel("x times (sec)")
    plt.ylabel("y reliabilities (%)")

    plt.show()


